package com.mycompany.a1;

public class Point {

	private float x;
	
	private float y;
	public Point(float x2, float y2) {
		this.x=x2;
		this.y=y2;
	}
	public float getX() {
		// TODO Auto-generated method stub
		return x;
	}
	public float getY() {
		// TODO Auto-generated method stub
		return y;
	}

	public void setX(float x) {
		this.x = x;
	}
	public void setY(float y) {
		this.y = y;
	}
}
